void vpnChangelog::on_btnOK_clicked()
{
    updateChangelogReadFlag();
    parentWidget()->close();
}

bool vpnChangelog::eventFilter(QObject *object, QEvent *event)
{
    if(object == parentWidget() && event->type() == QEvent::Close)
    {
        updateChangelogReadFlag();
    }

    return QWidget::eventFilter(object, event);
}
